xpath.script:
$(document).ready(function() {
    var product_id = odoo.context.active_id;
    $.getJSON('/product_turnover/data/' + product_id, function(data) {
        var ctx = document.getElementById('salesChart').getContext('2d');
        new Chart(ctx, {
            type: 'line',
            data: {
                labels: data.dates,
                datasets: [{
                    label: 'Sales',
                    data: data.sales,
                    borderColor: 'blue',
                    fill: false
                }]
            }
        });
    });
});
